import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { patientAPI } from "../../api/api";

function Medications({ user }) {
  const [medications, setMedications] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    if (!user) {
      navigate("/");
      return;
    }

    const fetchMedications = async () => {
      try {
        const res = await patientAPI.get(`medications/${user.patient_id}/`);
        setMedications(res.data);
        setLoading(false);
      } catch (err) {
        console.error("Error fetching medications:", err);
        setLoading(false);
      }
    };

    fetchMedications();
  }, [user, navigate]);

  if (!user) return null;

  if (loading) {
    return (
      <div className="container">
        <div className="card">
          <p>Loading...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="card">
        <div className="header">
          <h2>My Medications</h2>
          <div>
            <button className="btn btn-secondary" onClick={() => navigate("/portal/dashboard")}>
              Back to Dashboard
            </button>
            <button className="btn btn-secondary" onClick={() => navigate("/")}>
              Logout
            </button>
          </div>
        </div>

        {medications.length > 0 ? (
          <table>
            <thead>
              <tr>
                <th>Medication</th>
                <th>Dosage</th>
                <th>Frequency</th>
                <th>Prescribed By</th>
                <th>Start Date</th>
                <th>End Date</th>
                <th>Refills</th>
                <th>Instructions</th>
              </tr>
            </thead>
            <tbody>
              {medications.map((med) => (
                <tr key={med._id}>
                  <td><strong>{med.medication_name}</strong></td>
                  <td>{med.dosage}</td>
                  <td>{med.frequency}</td>
                  <td>{med.prescribed_by}</td>
                  <td>{new Date(med.start_date).toLocaleDateString()}</td>
                  <td>{med.end_date ? new Date(med.end_date).toLocaleDateString() : "Ongoing"}</td>
                  <td>
                    <span style={{
                      padding: '4px 8px',
                      borderRadius: '4px',
                      backgroundColor: med.refills_remaining === 0 ? '#f8d7da' : '#d4edda',
                      color: med.refills_remaining === 0 ? '#721c24' : '#155724'
                    }}>
                      {med.refills_remaining}
                    </span>
                  </td>
                  <td>{med.instructions || "N/A"}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p className="alert alert-info">No medications found.</p>
        )}
      </div>
    </div>
  );
}

export default Medications;
