import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { adminAPI } from "../../api/api";

function AppointmentList() {
  const [appointments, setAppointments] = useState([]);
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [selectedAppointment, setSelectedAppointment] = useState(null);
  const [formData, setFormData] = useState({
    patient_id: "",
    doctor_name: "",
    appointment_date: "",
    reason: "",
    status: "scheduled",
    notes: ""
  });

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [aptsRes, patientsRes] = await Promise.all([
        adminAPI.get("appointments/"),
        adminAPI.get("patients/")
      ]);
      setAppointments(aptsRes.data);
      setPatients(patientsRes.data);
      setLoading(false);
    } catch (err) {
      console.error("Error fetching data:", err);
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (selectedAppointment) {
        await adminAPI.put(`appointments/${selectedAppointment._id}/`, formData);
      } else {
        await adminAPI.post("appointments/", formData);
      }
      fetchData();
      closeModal();
    } catch (err) {
      alert("Error saving appointment: " + (err.response?.data?.message || err.message));
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this appointment?")) {
      try {
        await adminAPI.delete(`appointments/${id}/`);
        fetchData();
      } catch (err) {
        alert("Error deleting appointment: " + (err.response?.data?.message || err.message));
      }
    }
  };

  const openModal = (appointment = null) => {
    if (appointment) {
      setSelectedAppointment(appointment);
      // Convert datetime to datetime-local format
      const datetime = new Date(appointment.appointment_date);
      const localDatetime = new Date(datetime.getTime() - datetime.getTimezoneOffset() * 60000)
        .toISOString()
        .slice(0, 16);

      setFormData({
        patient_id: appointment.patient_id,
        doctor_name: appointment.doctor_name,
        appointment_date: localDatetime,
        reason: appointment.reason,
        status: appointment.status,
        notes: appointment.notes || ""
      });
    } else {
      setSelectedAppointment(null);
      setFormData({
        patient_id: "",
        doctor_name: "",
        appointment_date: "",
        reason: "",
        status: "scheduled",
        notes: ""
      });
    }
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    setSelectedAppointment(null);
  };

  const getPatientName = (patientId) => {
    const patient = patients.find(p => p._id === patientId);
    return patient ? `${patient.first_name} ${patient.last_name}` : "Unknown";
  };

  if (loading) {
    return (
      <div className="container">
        <div className="card">
          <p>Loading...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="card">
        <div className="header">
          <h2>Appointment Management</h2>
          <div>
            <button className="btn btn-success" onClick={() => openModal()}>
              Add New Appointment
            </button>
            <Link to="/admin" className="btn btn-secondary">
              Back to Dashboard
            </Link>
          </div>
        </div>

        {appointments.length > 0 ? (
          <table>
            <thead>
              <tr>
                <th>Patient</th>
                <th>Doctor</th>
                <th>Date & Time</th>
                <th>Reason</th>
                <th>Status</th>
                <th>Notes</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {appointments.map((apt) => (
                <tr key={apt._id}>
                  <td>{getPatientName(apt.patient_id)}</td>
                  <td>{apt.doctor_name}</td>
                  <td>{new Date(apt.appointment_date).toLocaleString()}</td>
                  <td>{apt.reason}</td>
                  <td>
                    <span style={{
                      padding: '4px 8px',
                      borderRadius: '4px',
                      backgroundColor: apt.status === 'scheduled' ? '#d1ecf1' : 
                                     apt.status === 'completed' ? '#d4edda' : '#fff3cd',
                      color: apt.status === 'scheduled' ? '#0c5460' : 
                             apt.status === 'completed' ? '#155724' : '#856404'
                    }}>
                      {apt.status}
                    </span>
                  </td>
                  <td>{apt.notes || "N/A"}</td>
                  <td>
                    <button className="btn btn-primary" onClick={() => openModal(apt)}>
                      Edit
                    </button>
                    <button className="btn btn-danger" onClick={() => handleDelete(apt._id)}>
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p className="alert alert-info">No appointments found.</p>
        )}
      </div>

      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <div className="modal-header">
              <h3>{selectedAppointment ? "Edit Appointment" : "Add New Appointment"}</h3>
              <button className="close" onClick={closeModal}>&times;</button>
            </div>
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label>Patient *</label>
                <select
                  name="patient_id"
                  value={formData.patient_id}
                  onChange={handleInputChange}
                  required
                >
                  <option value="">Select Patient</option>
                  {patients.map(p => (
                    <option key={p._id} value={p._id}>
                      {p.first_name} {p.last_name} ({p.email})
                    </option>
                  ))}
                </select>
              </div>
              <div className="form-group">
                <label>Doctor Name *</label>
                <input
                  type="text"
                  name="doctor_name"
                  value={formData.doctor_name}
                  onChange={handleInputChange}
                  placeholder="Dr. Smith"
                  required
                />
              </div>
              <div className="form-group">
                <label>Appointment Date & Time *</label>
                <input
                  type="datetime-local"
                  name="appointment_date"
                  value={formData.appointment_date}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Reason *</label>
                <input
                  type="text"
                  name="reason"
                  value={formData.reason}
                  onChange={handleInputChange}
                  placeholder="Annual checkup"
                  required
                />
              </div>
              <div className="form-group">
                <label>Status *</label>
                <select
                  name="status"
                  value={formData.status}
                  onChange={handleInputChange}
                  required
                >
                  <option value="scheduled">Scheduled</option>
                  <option value="completed">Completed</option>
                  <option value="cancelled">Cancelled</option>
                </select>
              </div>
              <div className="form-group">
                <label>Notes</label>
                <textarea
                  name="notes"
                  value={formData.notes}
                  onChange={handleInputChange}
                  rows="3"
                  placeholder="Additional notes..."
                />
              </div>
              <div style={{display: 'flex', gap: '10px', justifyContent: 'flex-end'}}>
                <button type="button" className="btn btn-secondary" onClick={closeModal}>
                  Cancel
                </button>
                <button type="submit" className="btn btn-success">
                  {selectedAppointment ? "Update" : "Create"}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default AppointmentList;
