import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { patientAPI } from "../../api/api";

function Appointments({ user }) {
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    if (!user) {
      navigate("/");
      return;
    }

    const fetchAppointments = async () => {
      try {
        const res = await patientAPI.get(`appointments/${user.patient_id}/`);
        setAppointments(res.data);
        setLoading(false);
      } catch (err) {
        console.error("Error fetching appointments:", err);
        setLoading(false);
      }
    };

    fetchAppointments();
  }, [user, navigate]);

  if (!user) return null;

  if (loading) {
    return (
      <div className="container">
        <div className="card">
          <p>Loading...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="card">
        <div className="header">
          <h2>My Appointments (90 Days)</h2>
          <div>
            <button className="btn btn-secondary" onClick={() => navigate("/portal/dashboard")}>
              Back to Dashboard
            </button>
            <button className="btn btn-secondary" onClick={() => navigate("/")}>
              Logout
            </button>
          </div>
        </div>

        {appointments.length > 0 ? (
          <table>
            <thead>
              <tr>
                <th>Date & Time</th>
                <th>Doctor</th>
                <th>Reason</th>
                <th>Status</th>
                <th>Notes</th>
              </tr>
            </thead>
            <tbody>
              {appointments.map((apt) => (
                <tr key={apt._id}>
                  <td>{new Date(apt.appointment_date).toLocaleString()}</td>
                  <td>{apt.doctor_name}</td>
                  <td>{apt.reason}</td>
                  <td>
                    <span style={{
                      padding: '4px 8px',
                      borderRadius: '4px',
                      backgroundColor: apt.status === 'scheduled' ? '#d1ecf1' : 
                                     apt.status === 'completed' ? '#d4edda' : '#fff3cd',
                      color: apt.status === 'scheduled' ? '#0c5460' : 
                             apt.status === 'completed' ? '#155724' : '#856404'
                    }}>
                      {apt.status}
                    </span>
                  </td>
                  <td>{apt.notes || "N/A"}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p className="alert alert-info">No appointments found in the last 90 days.</p>
        )}
      </div>
    </div>
  );
}

export default Appointments;
