import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { adminAPI } from "../../api/api";

function MedicationList() {
  const [medications, setMedications] = useState([]);
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [selectedMedication, setSelectedMedication] = useState(null);
  const [formData, setFormData] = useState({
    patient_id: "",
    medication_name: "",
    dosage: "",
    frequency: "",
    prescribed_by: "",
    start_date: "",
    end_date: "",
    refills_remaining: 0,
    instructions: ""
  });

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [medsRes, patientsRes] = await Promise.all([
        adminAPI.get("medications/"),
        adminAPI.get("patients/")
      ]);
      setMedications(medsRes.data);
      setPatients(patientsRes.data);
      setLoading(false);
    } catch (err) {
      console.error("Error fetching data:", err);
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (selectedMedication) {
        await adminAPI.put(`medications/${selectedMedication._id}/`, formData);
      } else {
        await adminAPI.post("medications/", formData);
      }
      fetchData();
      closeModal();
    } catch (err) {
      alert("Error saving medication: " + (err.response?.data?.message || err.message));
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this medication?")) {
      try {
        await adminAPI.delete(`medications/${id}/`);
        fetchData();
      } catch (err) {
        alert("Error deleting medication: " + (err.response?.data?.message || err.message));
      }
    }
  };

  const openModal = (medication = null) => {
    if (medication) {
      setSelectedMedication(medication);
      setFormData({
        patient_id: medication.patient_id,
        medication_name: medication.medication_name,
        dosage: medication.dosage,
        frequency: medication.frequency,
        prescribed_by: medication.prescribed_by,
        start_date: medication.start_date,
        end_date: medication.end_date || "",
        refills_remaining: medication.refills_remaining,
        instructions: medication.instructions || ""
      });
    } else {
      setSelectedMedication(null);
      setFormData({
        patient_id: "",
        medication_name: "",
        dosage: "",
        frequency: "",
        prescribed_by: "",
        start_date: "",
        end_date: "",
        refills_remaining: 0,
        instructions: ""
      });
    }
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    setSelectedMedication(null);
  };

  const getPatientName = (patientId) => {
    const patient = patients.find(p => p._id === patientId);
    return patient ? `${patient.first_name} ${patient.last_name}` : "Unknown";
  };

  if (loading) {
    return (
      <div className="container">
        <div className="card">
          <p>Loading...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="card">
        <div className="header">
          <h2>Medication Management</h2>
          <div>
            <button className="btn btn-success" onClick={() => openModal()}>
              Add New Medication
            </button>
            <Link to="/admin" className="btn btn-secondary">
              Back to Dashboard
            </Link>
          </div>
        </div>

        {medications.length > 0 ? (
          <table>
            <thead>
              <tr>
                <th>Patient</th>
                <th>Medication</th>
                <th>Dosage</th>
                <th>Frequency</th>
                <th>Prescribed By</th>
                <th>Start Date</th>
                <th>Refills</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {medications.map((med) => (
                <tr key={med._id}>
                  <td>{getPatientName(med.patient_id)}</td>
                  <td><strong>{med.medication_name}</strong></td>
                  <td>{med.dosage}</td>
                  <td>{med.frequency}</td>
                  <td>{med.prescribed_by}</td>
                  <td>{new Date(med.start_date).toLocaleDateString()}</td>
                  <td>{med.refills_remaining}</td>
                  <td>
                    <button className="btn btn-primary" onClick={() => openModal(med)}>
                      Edit
                    </button>
                    <button className="btn btn-danger" onClick={() => handleDelete(med._id)}>
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p className="alert alert-info">No medications found.</p>
        )}
      </div>

      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <div className="modal-header">
              <h3>{selectedMedication ? "Edit Medication" : "Add New Medication"}</h3>
              <button className="close" onClick={closeModal}>&times;</button>
            </div>
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label>Patient *</label>
                <select
                  name="patient_id"
                  value={formData.patient_id}
                  onChange={handleInputChange}
                  required
                >
                  <option value="">Select Patient</option>
                  {patients.map(p => (
                    <option key={p._id} value={p._id}>
                      {p.first_name} {p.last_name} ({p.email})
                    </option>
                  ))}
                </select>
              </div>
              <div className="form-group">
                <label>Medication Name *</label>
                <input
                  type="text"
                  name="medication_name"
                  value={formData.medication_name}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Dosage *</label>
                <input
                  type="text"
                  name="dosage"
                  value={formData.dosage}
                  onChange={handleInputChange}
                  placeholder="e.g., 500mg"
                  required
                />
              </div>
              <div className="form-group">
                <label>Frequency *</label>
                <input
                  type="text"
                  name="frequency"
                  value={formData.frequency}
                  onChange={handleInputChange}
                  placeholder="e.g., Twice daily"
                  required
                />
              </div>
              <div className="form-group">
                <label>Prescribed By *</label>
                <input
                  type="text"
                  name="prescribed_by"
                  value={formData.prescribed_by}
                  onChange={handleInputChange}
                  placeholder="Doctor name"
                  required
                />
              </div>
              <div className="form-group">
                <label>Start Date *</label>
                <input
                  type="date"
                  name="start_date"
                  value={formData.start_date}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>End Date (Optional)</label>
                <input
                  type="date"
                  name="end_date"
                  value={formData.end_date}
                  onChange={handleInputChange}
                />
              </div>
              <div className="form-group">
                <label>Refills Remaining *</label>
                <input
                  type="number"
                  name="refills_remaining"
                  value={formData.refills_remaining}
                  onChange={handleInputChange}
                  min="0"
                  required
                />
              </div>
              <div className="form-group">
                <label>Instructions</label>
                <textarea
                  name="instructions"
                  value={formData.instructions}
                  onChange={handleInputChange}
                  rows="3"
                  placeholder="Special instructions..."
                />
              </div>
              <div style={{display: 'flex', gap: '10px', justifyContent: 'flex-end'}}>
                <button type="button" className="btn btn-secondary" onClick={closeModal}>
                  Cancel
                </button>
                <button type="submit" className="btn btn-success">
                  {selectedMedication ? "Update" : "Create"}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default MedicationList;
